<script>
document.addEventListener("DOMContentLoaded", function() {
    // Parallax effect for floating backgrounds
    const parallaxContainer = document.getElementById('parallax-container');
    const parallaxElements = document.querySelectorAll('[data-speed]');
    
    if (parallaxContainer && parallaxElements.length > 0) {
        let mouseX = 0, mouseY = 0;
        let windowWidth = window.innerWidth;
        let windowHeight = window.innerHeight;
        
        // Update mouse position
        document.addEventListener('mousemove', (e) => {
            mouseX = e.clientX;
            mouseY = e.clientY;
            moveElements();
        });
        
        // Handle window resize
        window.addEventListener('resize', () => {
            windowWidth = window.innerWidth;
            windowHeight = window.innerHeight;
        });
        
        // Move elements based on mouse position
        function moveElements() {
            const centerX = windowWidth / 2;
            const centerY = windowHeight / 2;
            
            // Calculate distance from center
            const distX = mouseX - centerX;
            const distY = mouseY - centerY;
            
            parallaxElements.forEach(element => {
                const speed = parseFloat(element.getAttribute('data-speed'));
                
                // Apply transform based on mouse position and speed
                element.style.transform = `translate(${distX * speed}px, ${distY * speed}px)`;
            });
        }
        
        // Initial position
        moveElements();
    }
    
    // Animate counter elements when they come into view
    const counters = document.querySelectorAll('.text-3xl.font-bold');
    
    if (counters.length > 0) {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const value = entry.target.textContent;
                    const target = parseInt(value.replace(/\D/g, ''));
                    let count = 0;
                    
                    const updateCount = () => {
                        const increment = Math.ceil(target / 30);
                        if (count < target) {
                            count = Math.min(count + increment, target);
                            entry.target.textContent = count + '+';
                            requestAnimationFrame(updateCount);
                        }
                    };
                    
                    updateCount();
                    observer.unobserve(entry.target);
                }
            });
        }, {
            threshold: 0.5
        });
        
        counters.forEach(counter => {
            observer.observe(counter);
        });
    }
    
    // Add card hover effect with subtle 3D tilt
    const card = document.querySelector('.bg-white\\/5.backdrop-blur-md.rounded-2xl');
    
    if (card) {
        card.addEventListener('mousemove', (e) => {
            const rect = card.getBoundingClientRect();
            const x = e.clientX - rect.left;
            const y = e.clientY - rect.top;
            
            const centerX = rect.width / 2;
            const centerY = rect.height / 2;
            
            const moveX = (x - centerX) / 15;
            const moveY = (y - centerY) / 15;
            
            card.style.transform = `perspective(1000px) rotateX(${-moveY}deg) rotateY(${moveX}deg) scale3d(1.02, 1.02, 1.02)`;
        });
        
        card.addEventListener('mouseleave', () => {
            card.style.transform = 'perspective(1000px) rotateX(0) rotateY(0) scale3d(1, 1, 1)';
        });
    }
    
    // Add hover animations to category cards
    const categoryCards = document.querySelectorAll('.bg-white\\/5.hover\\:bg-white\\/10');
    
    categoryCards.forEach(card => {
        card.addEventListener('mouseenter', () => {
            card.style.transform = 'translateY(-8px)';
        });
        
        card.addEventListener('mouseleave', () => {
            card.style.transform = 'translateY(0)';
        });
    });
});
</script>